import React from "react";
import { Link } from "react-router-dom";
import Orb from "./Orb";

function Landing() {
  return (
    <div className="landing-page">
      {/* Orb Background */}
      <div className="orb-wrapper">
        <Orb
          hoverIntensity={0.4}
          rotateOnHover={true}
          hue={200}
          forceHoverState={false}
        />
      </div>

      {/* Math Background */}
      <div className="math-background">
        {Array.from({ length: 50 }).map((_, i) => (
          <span key={i} className="math-symbol">
            {["∑", "√", "π", "∞", "∫", "∆", "≈", "∇"][i % 8]}
          </span>
        ))}
      </div>

      {/* Main Content */}
      <main className="main-content">
        <section className="hero-section">
          <h1 className="hero-title">MathNarrator</h1>
          <p className="hero-subtitle">
            Deep-Dive Analysis into Contextual Scenario Generation from
            Mathematical Expressions
          </p>
          <div className="cta-buttons">
            <Link to="/login" className="btn">
              Login with Google
            </Link>
            <Link to="/chat" className="btn btn-outline">
              Try Demo
            </Link>
          </div>
        </section>
      </main>

      {/* Styles */}
      <style jsx="true">{`
        .landing-page {
          position: relative;
          min-height: 100vh;
          background: #000;
          color: #fff;
          font-family: "Helvetica Neue", Arial, sans-serif;
          overflow: hidden;
        }

        /* Orb layer */
        .orb-wrapper {
          position: fixed;
          top: 0;
          left: 0;
          width: 100%;
          height: 100vh;
          z-index: 1;
          pointer-events: none;
        }
        .orb-container {
          width: 100%;
          height: 100%;
        }

        /* Math symbols background */
        .math-background {
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          overflow: hidden;
          z-index: 0;
        }
        .math-symbol {
          position: absolute;
          font-size: 1.5rem;
          opacity: 0.15;
          animation: float 20s linear infinite;
          left: calc(100% * var(--x, 0));
          top: calc(100% * var(--y, 0));
        }
        @keyframes float {
          from {
            transform: translateY(100vh) rotate(0deg);
          }
          to {
            transform: translateY(-100vh) rotate(360deg);
          }
        }

        /* Main content */
        .main-content {
          position: relative;
          z-index: 10;
          display: flex;
          align-items: center;
          justify-content: center;
          min-height: 100vh;
          text-align: center;
          padding: 2rem;
        }

        .hero-section {
          max-width: 700px;
        }
        .hero-title {
          font-size: 3rem;
          font-weight: bold;
          margin-bottom: 1rem;
        }
        .hero-subtitle {
          font-size: 1.25rem;
          margin-bottom: 2rem;
          color: #ccc;
        }
        .cta-buttons {
          display: flex;
          gap: 1rem;
          justify-content: center;
          flex-wrap: wrap;
        }

        .btn {
          padding: 0.75rem 1.5rem;
          border: none;
          border-radius: 8px;
          background: #fff;
          color: #000;
          font-weight: bold;
          cursor: pointer;
          text-decoration: none;
          transition: all 0.3s ease;
        }
        .btn:hover {
          background: #f0f0f0;
        }
        .btn-outline {
          background: transparent;
          border: 2px solid #fff;
          color: #fff;
        }
        .btn-outline:hover {
          background: #fff;
          color: #000;
        }
      `}</style>
    </div>
  );
}

export default Landing;
