import React from "react";
import Orb from "./Orb";

function Landing() {
  const handleLogin = () => {
    // Redirect to Flask backend for Google OAuth
    window.location.href = "http://localhost:5000/auth/login";
  };

  const goToChat = () => {
    window.location.href = "/chat";
  };

  return (
    <div className="landing-page">
      {/* Orb Background */}
      <div className="orb-wrapper">
        <Orb
          hoverIntensity={0.4}
          rotateOnHover={true}
          hue={200}
          forceHoverState={false}
        />
      </div>

      {/* Math Background */}
      <div className="math-background">
        {Array.from({ length: 50 }).map((_, i) => (
          <span
            key={i}
            className="math-symbol"
            style={{
              "--x": Math.random(),
              "--y": Math.random(),
              animationDuration: `${10 + Math.random() * 20}s`,
              fontSize: `${1 + Math.random() * 2}rem`,
            }}
          >
            {["∑", "√", "π", "∞", "∫", "∆", "≈", "∇"][i % 8]}
          </span>
        ))}
      </div>

      {/* Main Content */}
      <main className="main-content">
        <div className="hero-section">
          <h1 className="hero-title">
            Math<span className="highlight">Narrator</span>
          </h1>
          <p className="hero-subtitle">
            A Deep-Dive into contextual scenario generation from mathematical
            expressions
          </p>

          {/* Buttons */}
          <div className="cta-buttons">
            <button className="btn-primary" onClick={handleLogin}>
              <span>Sign in with Google</span>
              <div className="btn-shine"></div>
            </button>

            <button className="btn-secondary" onClick={goToChat}>
              Open Chat <span className="arrow">→</span>
            </button>
          </div>
        </div>
      </main>

      {/* Styles */}
      <style jsx="true">{`
        .landing-page {
          position: fixed;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          background: #000;
          color: #fff;
          font-family: "Helvetica Neue", Arial, sans-serif;
          overflow: hidden;
          margin: 0;
          padding: 0;
        }

        /* Orb layer */
        .orb-wrapper {
          position: fixed;
          top: 0;
          left: 0;
          width: 100vw;
          height: 100vh;
          z-index: 1;
          pointer-events: none;
          display: flex;
          justify-content: center;
          align-items: center;
        }

        /* Math symbols */
        .math-background {
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          overflow: hidden;
          z-index: 0;
        }
        .math-symbol {
          position: absolute;
          font-size: 1.5rem;
          opacity: 0.15;
          animation: float 20s linear infinite;
          left: calc(100% * var(--x, 0));
          top: calc(100% * var(--y, 0));
        }
        @keyframes float {
          from {
            transform: translateY(100vh) rotate(0deg);
          }
          to {
            transform: translateY(-100vh) rotate(360deg);
          }
        }

        /* Content */
        .main-content {
          position: relative;
          z-index: 10;
          display: flex;
          align-items: center;
          justify-content: center;
          min-height: 100vh;
          text-align: center;
          padding: 2rem;
        }

        .hero-section {
          animation: fadeInUp 1.5s ease forwards;
          opacity: 0;
        }
        @keyframes fadeInUp {
          from {
            opacity: 0;
            transform: translateY(40px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        .hero-title {
          font-size: clamp(2.5rem, 5vw, 4rem);
          font-weight: 800;
          margin-bottom: 1rem;
        }

        .highlight {
          color: #00e5ff;
