import React, { useEffect, useState } from 'react';

export default function Landing() {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  const handleLogin = () => {
    window.location.href = "http://localhost:5000/auth/login";
  };

  const goToChat = () => {
    const token = localStorage.getItem('mathnarrator_token');
    if (token) {
      // navigate('/chat') - would work with router
      alert('Navigating to chat...');
    } else {
      alert('Please login first (use Google).');
    }
  };

  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({ x: e.clientX, y: e.clientY });
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  const mathSymbols = ['∫', '∑', '∏', '∂', '∇', '∞', 'π', 'α', 'β', 'γ', 'δ', 'ε', 'θ', 'λ', 'μ', 'σ', 'φ', 'ψ', 'ω', '√', '∆', '∀', '∃', '⊂', '⊃', '∈', '∉', '≡', '≠', '≤', '≥', '∧', '∨', '⟨', '⟩', '∮', '⊕', '⊗'];

  return (
    <div className="landing-container">
      {/* Mathematical Background */}
      <div className="math-background">
        {mathSymbols.map((symbol, index) => (
          <div
            key={index}
            className="math-symbol"
            style={{
              left: `${(index * 137) % 100}%`,
              top: `${(index * 47) % 100}%`,
              animationDelay: `${index * 0.5}s`,
              fontSize: `${Math.random() * 2 + 1}rem`,
              opacity: 0.05 + Math.random() * 0.1
            }}
          >
            {symbol}
          </div>
        ))}
      </div>

      {/* Cursor follower */}
      <div 
        className="cursor-follower"
        style={{
          left: mousePosition.x - 10,
          top: mousePosition.y - 10,
        }}
      />

      {/* Header */}
      <header className="header">
        <div className="nav-brand">MathNarrator</div>
      </header>

      {/* Main Content */}
      <main className="main-content">
        <div className="hero-section">
          <h1 className="hero-title">
            Math<span className="highlight">Narrator</span>
          </h1>

          <p className="hero-subtitle">
            A Deep-Dive into contextual scenario generation from mathematical expressions
          </p>

          <div className="cta-buttons">
            <button className="btn-primary" onClick={handleLogin}>
              <span>Sign in with Google</span>
              <div className="btn-shine"></div>
            </button>
            <button className="btn-secondary" onClick={goToChat}>
              Open Chat
              <span className="arrow">→</span>
            </button>
          </div>
        </div>

        <div className="about-section">
          <div className="section-header">
            <h2>About</h2>
            <div className="section-line"></div>
          </div>

          <p className="about-text">
            Type a math expression and see contextual story-style scenarios generated. 
            Transform abstract mathematical concepts into engaging narratives.
          </p>

          <div className="feature-grid">
            <div className="feature-card">
              <div className="feature-icon">∫</div>
              <h3>Calculus Stories</h3>
              <p>Turn integrals into real-world scenarios</p>
            </div>
            <div className="feature-card">
              <div className="feature-icon">∑</div>
              <h3>Series Narratives</h3>
              <p>Transform sequences into compelling tales</p>
            </div>
            <div className="feature-card">
              <div className="feature-icon">∇</div>
