import React from 'react'
import { useNavigate } from 'react-router-dom'

export default function Landing(){
  const navigate = useNavigate()

  const handleLogin = () => {
    // Redirect to Flask backend which starts Google OAuth flow
    window.location.href = "http://localhost:5000/auth/login"
  }

  const goToChat = () => {
    // check token
    const token = localStorage.getItem('mathnarrator_token')
    if(token){
      navigate('/chat')
    } else {
      alert('Please login first (use Google).')
    }
  }

  return (
    <div>
      <h1>MathNarrator</h1>
      <p>A Deep-Dive into contextual scenario generation from mathematical expressions.</p>

      <div style={{marginTop: '1.2rem'}}>
        <button className="button" onClick={handleLogin}>Sign in with Google</button>
        <button className="button-outline" onClick={goToChat}>Open Chat</button>
      </div>

      <section style={{marginTop: '2rem'}}>
        <h3>About</h3>
        <p>Type a math expression and see contextual story-style scenarios generated. (Currently responses are hardcoded.)</p>
      </section>
    </div>
  )
}
