import React, { useEffect, useState } from 'react';

export default function Landing() {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [angle, setAngle] = useState(0);

  const handleLogin = () => {
    window.location.href = "http://localhost:5000/auth/login";
  };

  const goToChat = () => {
    const token = localStorage.getItem('mathnarrator_token');
    if (token) {
      alert('Navigating to chat...');
    } else {
      alert('Please login first (use Google).');
    }
  };

  // Continuous animation loop
  useEffect(() => {
    let frame;
    const animate = () => {
      setAngle((prev) => {
        const next = prev + 0.02; // speed
        setMousePosition({
          x: window.innerWidth / 2 + Math.cos(next) * 150, // radius
          y: window.innerHeight / 2 + Math.sin(next) * 150
        });
        return next;
      });
      frame = requestAnimationFrame(animate);
    };
    frame = requestAnimationFrame(animate);

    return () => cancelAnimationFrame(frame);
