import React, { useEffect, useState } from "react";

export default function Landing() {
  const [mousePosition, setMousePosition] = useState({
    x: window.innerWidth / 2,
    y: window.innerHeight / 2,
  });
  const [target, setTarget] = useState({
    x: window.innerWidth / 2,
    y: window.innerHeight / 2,
  });
  const [isGoogleClicked, setIsGoogleClicked] = useState(false);

  const handleLogin = () => {
    setIsGoogleClicked(true);
    setTimeout(() => {
      window.location.href = "http://localhost:5000/auth/login";
    }, 300);
  };

  const goToChat = () => {
    const token = localStorage.getItem("mathnarrator_token");
    if (token) {
      alert("Navigating to chat...");
    } else {
      alert("Please login first (use Google).");
    }
  };

  // Smooth wandering animation
  useEffect(() => {
    let frame;

    const animate = () => {
      setMousePosition((prev) => {
        const speed = 0.0002;
        const dx = target.x - prev.x;
        const dy = target.y - prev.y;
        const dist = Math.sqrt(dx * dx + dy * dy);

        if (dist < 2) {
          setTarget({
            x: Math.random() * window.innerWidth,
            y: Math.random() * window.innerHeight,
          });
        }

        return {
          x: prev.x + dx * speed,
          y: prev.y + dy * speed,
        };
      });

      frame = requestAnimationFrame(animate);
    };

    frame = requestAnimationFrame(animate);
    return () => cancelAnimationFrame(frame);
  }, [target]);

  const mathSymbols = [
    "∫", "∑", "∏", "∂", "∇", "∞", "π", "α", "β", "γ", "δ", "ε", "θ", "λ", "μ", "σ", "φ", "ψ", "ω", "√", "∆", "∀", "∃", "⊂", "⊃", "∈", "∉", "≡", "≠", "≤", "≥", "∧", "∨", "⟨", "⟩", "∮", "⊕", "⊗"
  ];

  return (
    <div className="landing-container">
      <div className="math-background">
        {mathSymbols.map((symbol, index) => (
          <div
            key={index}
            className="math-symbol"
            style={{
              left: `${(index * 137) % 100}%`,
              top: `${(index * 47) % 100}%`,
              animationDelay: `${index * 0.3}s`,
              fontSize: `${Math.random() * 1.5 + 0.8}rem`,
              opacity: 0.06 + Math.random() * 0.08,
              animationDuration: `${15 + Math.random() * 10}s`,
            }}
          >
            {symbol}
          </div>
        ))}
      </div>

      <div
        className="cursor-follower"
        style={{
          left: mousePosition.x,
          top: mousePosition.y,
        }}
      />

      <main className="main-content">
        <div className="hero-section">
          <div className="gradient-backdrop"></div>
          <h1 className="hero-title">
            Math<span className="highlight">Narrator</span>
          </h1>
          <p className="hero-subtitle">
            A Deep-Dive into contextual scenario generation from mathematical expressions
          </p>
          <div className="cta-buttons">
            <button 
              className={`btn-primary ${isGoogleClicked ? 'google-colors' : ''}`} 
              onClick={handleLogin}
            >
              <span>Sign in with Google</span>
              <div className="btn-shine"></div>
            </button>
            <button className="btn-primary" onClick={goToChat}>
              Quick Chat
            </button>
          </div>
        </div>

        <div className="about-section">
          <div className="section-header">
            <h2>About</h2>
            <div className="section-line"></div>
          </div>

          <p className="about-text">
            Transform abstract mathematical concepts into engaging, contextual narratives. 
            MathNarrator bridges the gap between complex equations and real-world understanding 
            by generating story-style scenarios that make mathematics accessible and intuitive.
          </p>

          <div className="feature-grid">
            <div className="feature-card">
              <div className="feature-icon">∫</div>
              <h3>Calculus Stories</h3>
              <p>Turn integrals and derivatives into real-world scenarios that make sense</p>
            </div>
            <div className="feature-card">
              <div className="feature-icon">∑</div>
              <h3>Series Narratives</h3>
              <p>Transform sequences and series into compelling mathematical tales</p>
            </div>
            <div className="feature-card">
              <div className="feature-icon">∇</div>
              <h3>Vector Adventures</h3>
              <p>Convert gradients and vectors into journey-based learning experiences</p>
            </div>
          </div>
        </div>
      </main>

      <style jsx>{`
        * {
          box-sizing: border-box;
          margin: 0;
          padding: 0;
        }

        .landing-container {
          min-height: 100vh;
          background: #fff;
          color: #000;
          font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif;
          position: relative;
          overflow-x: hidden;
        }

        .math-background {
          position: fixed;
          top: 0;
          left: 0;
          width: 100vw;
          height: 100vh;
          pointer-events: none;
          z-index: 0;
        }

        .math-symbol {
          position: absolute;
          font-size: 2rem;
          font-weight: 300;
          color: #000;
          animation: smoothFlow linear infinite;
        }

        @keyframes smoothFlow {
          0% { 
            transform: translate(0, 0) rotate(0deg); 
          }
          25% { 
            transform: translate(20px, -30px) rotate(90deg); 
          }
          50% { 
            transform: translate(-15px, -60px) rotate(180deg); 
          }
          75% { 
            transform: translate(-35px, -30px) rotate(270deg); 
          }
          100% { 
            transform: translate(0, 0) rotate(360deg); 
          }
        }

        .cursor-follower {
          position: fixed;
          width: 20px;
          height: 20px;
          border-radius: 50%;
          background: radial-gradient(
            circle,
            rgba(0, 0, 0, 0.15) 0%,
            transparent 70%
          );
          pointer-events: none;
          transform: translate(-50%, -50%);
          z-index: 9999;
        }

        .main-content {
          position: relative;
          z-index: 10;
          max-width: 1200px;
          margin: 0 auto;
          padding: 0 2rem;
        }

        .hero-section {
          text-align: center;
          padding: 6rem 0;
          position: relative;
        }

        .gradient-backdrop {
          position: absolute;
          top: 50%;
          left: 50%;
          transform: translate(-50%, -50%);
          width: 80%;
          height: 60%;
          background: linear-gradient(135deg, rgba(0,0,0,0.02) 0%, rgba(0,0,0,0.08) 50%, rgba(0,0,0,0.02) 100%);
          border-radius: 20px;
          z-index: -1;
        }

        .hero-title {
