import React from "react";
import { Link } from "react-router-dom";
import Orb from "./Orb";

function Landing() {
  return (
    <div className="landing-page">
      {/* Orb Background */}
      <div className="orb-wrapper">
        <Orb
          hoverIntensity={0.4}
          rotateOnHover={true}
          hue={200}
          forceHoverState={false}
        />
      </div>

      {/* Math Background */}
      <div className="math-background">
  {Array.from({ length: 50 }).map((_, i) => (
    <span
      key={i}
      className="math-symbol"
      style={{
        "--x": Math.random(),
        "--y": Math.random(),
        animationDuration: `${10 + Math.random() * 20}s`,
        fontSize: `${1 + Math.random() * 2}rem`,
      }}
    >
      {["∑", "√", "π", "∞", "∫", "∆", "≈", "∇"][i % 8]}
    </span>
  ))}
</div>


      {/* Main Content */}
      <main className="main-content">
        <section className="hero-section">
          <h1 className="hero-title">MathNarrator</h1>
          <p className="hero-subtitle">
            Deep-Dive Analysis into Contextual Scenario Generation from
            Mathematical Expressions
          </p>
          <div className="cta-buttons">
            <Link to="/login" className="btn">
              Login with Google
            </Link>
            <Link to="/chat" className="btn btn-outline">
              Try Demo
            </Link>
          </div>
        </section>
      </main>

      {/* Styles */}
      <style jsx="true">{`
        .landing-page {
  position: relative;
  width: 100vw;   /* force full width */
  height: 100vh;  /* force full height */
  background: #000;
  color: #fff;
  font-family: "Helvetica Neue", Arial, sans-serif;
  overflow: hidden;
  margin: 0;      /* reset any default margins */
  padding: 0;
}


        /* Orb layer */
        .orb-wrapper {
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;  /* instead of vh */
  height: 100vh;
  z-index: 1;
  pointer-events: none;
  display: flex;
  justify-content: center;
  align-items: center;
}

        .orb-container {
          width: 100vh;
          height: 100vh;
        }

        /* Math symbols background */
        .math-background {
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          overflow: hidden;
          z-index: 0;
        }
        .math-symbol {
          position: absolute;
          font-size: 1.5rem;
          opacity: 0.15;
          animation: float 20s linear infinite;
          left: calc(100% * var(--x, 0));
          top: calc(100% * var(--y, 0));
        }
        @keyframes float {
          from {
            transform: translateY(100vh) rotate(0deg);
          }
          to {
            transform: translateY(-100vh) rotate(360deg);
          }
        }

        /* Main content */
        .main-content {
          position: relative;
          z-index: 10;
          display: flex;
          align-items: center;
          justify-content: center;
          min-height: 100vh;
          text-align: center;
          padding: 2rem;
        }

        .hero-section {
  animation: fadeInUp 1.5s ease forwards;
  opacity: 0;
}
@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(40px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

        .hero-title {
  font-size: clamp(2.5rem, 5vw, 4rem);
  font-weight: 800;
  margin-bottom: 1rem;
}

.hero-subtitle {
  font-size: clamp(1rem, 2vw, 1.5rem);
  margin-bottom: 2.5rem;
  color: #bbb;
  line-height: 1.6;
}

        .cta-buttons {
          display: flex;
          gap: 1rem;
          justify-content: center;
          flex-wrap: wrap;
        }

        .btn {
  padding: 0.85rem 1.75rem;
  border-radius: 10px;
  font-weight: 600;
  transition: all 0.3s ease;
  box-shadow: 0 0 10px rgba(255,255,255,0.2);
}
.btn:hover {
  transform: translateY(-3px);
  box-shadow: 0 0 20px rgba(255,255,255,0.4);
}

        .btn-outline {
          background: transparent;
          border: 2px solid #fff;
          color: #fff;
        }
        .btn-outline:hover {
          background: #fff;
          color: #000;
        }
      `}</style>
    </div>
  );
}

export default Landing;
